import com.example.fucarrentingsystem.entity.*;
import com.example.fucarrentingsystem.service.*;
import com.example.fucarrentingsystem.util.HibernateUtil;
import jakarta.persistence.EntityManager;
import java.time.LocalDate;

public class TestCarRental {
    public static void main(String[] args) {
        System.out.println("=== Test Car Rental System ===");

        try {
            // Test database connection
            System.out.println("1. Testing database connection...");
            EntityManager em = HibernateUtil.getEntityManager();
            System.out.println("✓ Database connection successful");
            em.close();

            // Test services
            System.out.println("\n2. Testing services...");
            CarService carService = new CarService();
            CustomerService customerService = new CustomerService();
            CarRentalService carRentalService = new CarRentalService();

            // Get sample data
            System.out.println("3. Loading sample data...");
            var cars = carService.findAll();
            var customers = customerService.findAll();

            System.out.println("Found " + cars.size() + " cars");
            System.out.println("Found " + customers.size() + " customers");

            if (cars.isEmpty()) {
                System.out.println("⚠ No cars found in database");
                return;
            }

            if (customers.isEmpty()) {
                System.out.println("⚠ No customers found in database");
                return;
            }

            // Test rental creation
            System.out.println("\n4. Testing rental creation...");
            Car testCar = cars.stream()
                .filter(car -> "Available".equals(car.getStatus()))
                .findFirst()
                .orElse(null);

            if (testCar == null) {
                System.out.println("⚠ No available cars found");
                return;
            }

            Customer testCustomer = customers.get(0);

            System.out.println("Test car: " + testCar.getCarName() + " (ID: " + testCar.getCarID() + ")");
            System.out.println("Test customer: " + testCustomer.getCustomerName() + " (ID: " + testCustomer.getCustomerID() + ")");

            // Create test rental
            CarRental testRental = new CarRental();
            testRental.setCustomer(testCustomer);
            testRental.setCar(testCar);
            testRental.setPickupDate(LocalDate.now().plusDays(1));
            testRental.setReturnDate(LocalDate.now().plusDays(3));
            testRental.setRentPrice(testCar.getRentPrice() * 2);
            testRental.setStatus("Active");

            System.out.println("Attempting to save rental...");
            CarRental savedRental = carRentalService.save(testRental);
            System.out.println("✓ Rental saved successfully with ID: " + savedRental.getRentalID());

            // Test conflict detection
            System.out.println("\n5. Testing conflict detection...");
            boolean hasConflict = carRentalService.hasRentalConflict(
                testCar.getCarID(), 
                LocalDate.now().plusDays(2), 
                LocalDate.now().plusDays(4)
            );
            System.out.println("Conflict detected: " + hasConflict);

            System.out.println("\n✓ All tests passed!");

        } catch (Exception e) {
            System.err.println("❌ Error occurred: " + e.getMessage());
            e.printStackTrace();
        }
    }
}
